mapper.select:

select id, conversation_id, from_id, to_id, type, status, is_push, other_msg, time,
content
from message where type = 1 and is_push = 0 and to_id = #{id}


select id, conversation_id, from_id, to_id, type, status, is_push, other_msg, time,
content
from message where type = 1 and is_push = 1 and to_id = #{id}


select id, conversation_id, from_id, to_id, type, status, is_push, other_msg, time,
content
from message where type != 1 and is_push = 1 and to_id = #{id}


select id, conversation_id, from_id, to_id, type, status, is_push, other_msg, time,
content
from message where type != 1  and  conversation_id = #{id}
